mapper.select:
SELECT * FROM member
WHERE member_id = #{memberId}

	AND member_pw = #{memberPw}


	AND member_tel = #{memberTel}

mapper.update:

UPDATE member

	MEMBER_NAME = #{memberName}, 
	MEMBER_TEL = #{memberTel}, 
	MEMBER_ADDR = #{memberAddr}, 

	MEMBER_PW = #{memberPw} 



WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM member
 WHERE member_id = #{memberId} 
   and member_pw =#{memberPw}
